import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchBills, addBill, updateBill, removeBill } from '../redux/billSlice'
import api from '../services/api'

function BillRow({ b, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    water_amount: b.water_amount,
    electricity_amount: b.electricity_amount,
    balance: b.balance,
    paid_status: b.paid_status,
  })

  const save = async () => {
    await onSave(b.id, form)
    setEditing(false)
  }

  if (editing) {
    return (
      <tr className="border-t">
        <td className="px-4 py-2">{b.member}</td>
        <td className="px-4 py-2">{b.month}</td>
        <td className="px-4 py-2"><input className="border p-1" type="number" step="0.01" value={form.water_amount} onChange={e => setForm({ ...form, water_amount: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1" type="number" step="0.01" value={form.electricity_amount} onChange={e => setForm({ ...form, electricity_amount: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1" type="number" step="0.01" value={form.balance} onChange={e => setForm({ ...form, balance: e.target.value })} /></td>
        <td className="px-4 py-2">
          <select className="border p-1" value={form.paid_status} onChange={e => setForm({ ...form, paid_status: e.target.value })}>
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <button onClick={save} className="mr-2 text-sm text-green-600">Save</button>
          <button onClick={() => setEditing(false)} className="text-sm text-gray-600">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t">
      <td className="px-4 py-2">{b.member}</td>
      <td className="px-4 py-2">{b.month}</td>
      <td className="px-4 py-2">₹{b.water_amount}</td>
      <td className="px-4 py-2">₹{b.electricity_amount}</td>
      <td className="px-4 py-2">₹{b.balance}</td>
      <td className="px-4 py-2"><span className={b.paid_status === 'Paid' ? 'text-green-600' : 'text-red-600'}>{b.paid_status}</span></td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600">Edit</button>
        <button onClick={() => onDelete(b.id)} className="text-sm text-red-600">Delete</button>
      </td>
    </tr>
  )
}

export default function Bills() {
  const dispatch = useDispatch()
  const { list: bills, status } = useSelector(state => state.bills)
  const [form, setForm] = useState({ member: '', month: '', water_amount: 0, electricity_amount: 0, balance: 0, paid_status: 'Unpaid' })

  useEffect(() => {
    dispatch(fetchBills())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    try {
      const billData = { ...form, member: parseInt(form.member) || null }
      const res = await api.post('/bills/', billData)
      dispatch(addBill(res.data))
      setForm({ member: '', month: '', water_amount: 0, electricity_amount: 0, balance: 0, paid_status: 'Unpaid' })
    } catch (err) { console.error(err) }
  }

  const remove = async (id) => {
    if (!confirm('Delete bill?')) return
    await api.delete(`/bills/${id}/`)
    dispatch(removeBill(id))
  }

  const save = async (id, data) => {
    const res = await api.patch(`/bills/${id}/`, data)
    dispatch(updateBill(res.data))
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Bills</h2>

      <form onSubmit={create} className="mb-6 space-y-2 max-w-lg">
        <div>
          <input className="border p-2 w-full" placeholder="Member ID" type="number" value={form.member} onChange={e => setForm({ ...form, member: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Month (e.g., Nov-2025)" value={form.month} onChange={e => setForm({ ...form, month: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Water Amount" type="number" step="0.01" value={form.water_amount} onChange={e => setForm({ ...form, water_amount: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Electricity Amount" type="number" step="0.01" value={form.electricity_amount} onChange={e => setForm({ ...form, electricity_amount: e.target.value })} />
        </div>
        <button className="bg-green-600 text-white px-3 py-1 rounded">Add Bill</button>
      </form>

      {loading ? <div>Loading...</div> : (
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Member</th>
              <th className="px-4 py-2">Month</th>
              <th className="px-4 py-2">Water</th>
              <th className="px-4 py-2">Electricity</th>
              <th className="px-4 py-2">Balance</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {bills.map(b => (
              <BillRow key={b.id} b={b} onDelete={remove} onSave={save} />
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
