import React, {useEffect, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchMembers, addMember, updateMember, removeMember } from '../redux/memberSlice'
import api from '../services/api'

function MemberRow({m, onDelete, onSave}){
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({name: m.name, email: m.email, room_number: m.room_number || ''})
  const [saving, setSaving] = useState(false)

  const save = async ()=>{
    setSaving(true)
    try {
      await onSave(m.id, form)
      setEditing(false)
    } catch (err) {
      console.error('Error saving member:', err)
    } finally {
      setSaving(false)
    }
  }

  if(editing){
    return (
      <tr className="border-t bg-blue-50">
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.room_number} onChange={e=>setForm({...form, room_number:e.target.value})} /></td>
        <td className="px-4 py-2">
          <button onClick={save} disabled={saving} className="mr-2 text-sm text-green-600 hover:text-green-800 disabled:text-gray-400">{saving ? 'Saving...' : 'Save'}</button>
          <button onClick={()=>setEditing(false)} disabled={saving} className="text-sm text-gray-600 hover:text-gray-800 disabled:text-gray-400">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t hover:bg-gray-50">
      <td className="px-4 py-2">{m.name}</td>
      <td className="px-4 py-2">{m.email}</td>
      <td className="px-4 py-2">{m.room_number || '-'}</td>
      <td className="px-4 py-2">
        <button onClick={()=>setEditing(true)} className="mr-2 text-sm text-blue-600 hover:text-blue-800">Edit</button>
        <button onClick={()=>onDelete(m.id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
      </td>
    </tr>
  )
}

export default function Members(){
  const dispatch = useDispatch()
  const { list: members, status, error } = useSelector(state => state.members)
  const [form, setForm] = useState({name:'', email:'', room_number:''})
  const [submitError, setSubmitError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(()=>{
    console.log('[MEMBERS] Fetching members...')
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e)=>{
    e.preventDefault()
    setSubmitting(true)
    setSubmitError(null)
    try{
      console.log('[MEMBERS] Creating new member:', form)
      const res = await api.post('/members/', form)
      dispatch(addMember(res.data))
      setForm({name:'', email:'', room_number:''})
      console.log('[MEMBERS] Member created successfully')
    }catch(err){ 
      console.error('[MEMBERS] Error creating member:', err.response?.data || err.message)
      setSubmitError('Failed to create member: ' + (err.response?.data?.detail || err.message))
    }finally{
      setSubmitting(false)
    }
  }

  const remove = async (id)=>{
    if(!confirm('Delete this member?')) return
    try {
      console.log('[MEMBERS] Deleting member:', id)
      await api.delete(`/members/${id}/`)
      dispatch(removeMember(id))
      console.log('[MEMBERS] Member deleted successfully')
    } catch (err) {
      console.error('[MEMBERS] Error deleting member:', err)
      alert('Failed to delete member')
    }
  }

  const save = async (id, data)=>{
    try {
      console.log('[MEMBERS] Updating member:', id, data)
      const res = await api.patch(`/members/${id}/`, data)
      dispatch(updateMember(res.data))
      console.log('[MEMBERS] Member updated successfully')
    } catch (err) {
      console.error('[MEMBERS] Error updating member:', err)
      alert('Failed to update member')
    }
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Members</h2>

      <form onSubmit={create} className="mb-8 space-y-3 max-w-lg bg-gray-50 p-4 rounded">
        <h3 className="font-semibold">Add New Member</h3>
        <div>
          <label className="block text-sm font-medium mb-1">Name *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="John Doe"
            value={form.name} 
            onChange={e=>setForm({...form, name: e.target.value})}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Email *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="john@example.com"
            type="email"
            value={form.email} 
            onChange={e=>setForm({...form, email: e.target.value})}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Room Number</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="101"
            value={form.room_number} 
            onChange={e=>setForm({...form, room_number: e.target.value})}
            disabled={submitting}
          />
        </div>
        {submitError && <div className="text-red-600 bg-red-50 p-2 rounded text-sm">{submitError}</div>}
        <div>
          <button 
            type="submit"
            disabled={submitting || !form.name || !form.email}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {submitting ? 'Adding...' : 'Add Member'}
          </button>
        </div>
      </form>

      {error && <div className="text-red-600 bg-red-50 p-4 rounded mb-4">{error}</div>}
      
      {loading ? (
        <div className="text-center py-8">
          <div className="inline-block">Loading members...</div>
        </div>
      ) : members.length === 0 ? (
        <div className="text-center py-8 text-gray-600">No members yet. Add one to get started!</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 border-b">
                <th className="px-4 py-3 text-left">Name</th>
                <th className="px-4 py-3 text-left">Email</th>
                <th className="px-4 py-3 text-left">Room</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {members.map(m => (
                <MemberRow key={m.id} m={m} onDelete={remove} onSave={save} />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
