import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchBills, addBill, updateBill, removeBill } from '../redux/billSlice'
import { fetchMembers } from '../redux/memberSlice'
import api from '../services/api'

function BillRow({ b, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    water_amount: b.water_amount,
    electricity_amount: b.electricity_amount,
    balance: b.balance,
    paid_status: b.paid_status,
  })

  const save = async () => {
    await onSave(b.id, form)
    setEditing(false)
  }

  const memberDisplay = b.member_name || (typeof b.member === 'object' ? b.member?.name : b.member) || 'N/A'

  if (editing) {
    return (
      <tr className="border-t bg-blue-50">
        <td className="px-4 py-2">{memberDisplay}</td>
        <td className="px-4 py-2">{b.month}</td>
        <td className="px-4 py-2"><input className="border p-1 w-full" type="number" step="0.01" value={form.water_amount} onChange={e => setForm({ ...form, water_amount: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" type="number" step="0.01" value={form.electricity_amount} onChange={e => setForm({ ...form, electricity_amount: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" type="number" step="0.01" value={form.balance} onChange={e => setForm({ ...form, balance: e.target.value })} /></td>
        <td className="px-4 py-2">
          <select className="border p-1 w-full" value={form.paid_status} onChange={e => setForm({ ...form, paid_status: e.target.value })}>
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <button onClick={save} className="mr-2 text-sm text-green-600 hover:text-green-800">Save</button>
          <button onClick={() => setEditing(false)} className="text-sm text-gray-600 hover:text-gray-800">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t hover:bg-gray-50">
      <td className="px-4 py-2">{memberDisplay}</td>
      <td className="px-4 py-2">{b.month}</td>
      <td className="px-4 py-2">₹{b.water_amount}</td>
      <td className="px-4 py-2">₹{b.electricity_amount}</td>
      <td className="px-4 py-2">₹{b.balance}</td>
      <td className="px-4 py-2"><span className={b.paid_status === 'Paid' ? 'text-green-600' : 'text-red-600'}>{b.paid_status}</span></td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600 hover:text-blue-800">Edit</button>
        <button onClick={() => onDelete(b.id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
      </td>
    </tr>
  )
}

export default function Bills() {
  const dispatch = useDispatch()
  const { list: bills, status } = useSelector(state => state.bills)
  const { list: members } = useSelector(state => state.members)
  const [form, setForm] = useState({ member: '', month: '', water_amount: 0, electricity_amount: 0, balance: 0, paid_status: 'Unpaid' })
  const [submitError, setSubmitError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    dispatch(fetchBills())
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setSubmitError(null)
    try {
      const billData = { ...form, member: parseInt(form.member) || null }
      const res = await api.post('/bills/', billData)
      dispatch(addBill(res.data))
      setForm({ member: '', month: '', water_amount: 0, electricity_amount: 0, balance: 0, paid_status: 'Unpaid' })
    } catch (err) {
      console.error('[BILLS] Error creating bill:', err.response?.data || err.message)
      setSubmitError('Failed to create bill: ' + (err.response?.data?.detail || err.message))
    } finally {
      setSubmitting(false)
    }
  }

  const remove = async (id) => {
    if (!confirm('Delete bill?')) return
    try {
      await api.delete(`/bills/${id}/`)
      dispatch(removeBill(id))
    } catch (err) {
      console.error('[BILLS] Error deleting bill:', err)
      alert('Failed to delete bill')
    }
  }

  const save = async (id, data) => {
    try {
      const res = await api.patch(`/bills/${id}/`, data)
      dispatch(updateBill(res.data))
    } catch (err) {
      console.error('[BILLS] Error updating bill:', err)
      alert('Failed to update bill')
    }
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Bills</h2>

      <form onSubmit={create} className="mb-8 space-y-3 max-w-lg bg-gray-50 p-4 rounded">
        <h3 className="font-semibold">Add New Bill</h3>
        <div>
          <label className="block text-sm font-medium mb-1">Member *</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.member} 
            onChange={e => setForm({ ...form, member: e.target.value })}
            required
            disabled={submitting}
          >
            <option value="">Select a member</option>
            {members.map(m => (
              <option key={m.id} value={m.id}>{m.name} {m.room_number ? `(Room ${m.room_number})` : ''}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Month *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Month (e.g., Nov-2025)" 
            value={form.month} 
            onChange={e => setForm({ ...form, month: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Water Amount</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Water Amount" 
            type="number" 
            step="0.01" 
            value={form.water_amount} 
            onChange={e => setForm({ ...form, water_amount: e.target.value })}
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Electricity Amount</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Electricity Amount" 
            type="number" 
            step="0.01" 
            value={form.electricity_amount} 
            onChange={e => setForm({ ...form, electricity_amount: e.target.value })}
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Balance</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Balance" 
            type="number" 
            step="0.01" 
            value={form.balance} 
            onChange={e => setForm({ ...form, balance: e.target.value })}
            disabled={submitting}
          />
        </div>
        {submitError && <div className="text-red-600 bg-red-50 p-2 rounded text-sm">{submitError}</div>}
        <div>
          <button 
            type="submit"
            disabled={submitting || !form.member || !form.month}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {submitting ? 'Adding...' : 'Add Bill'}
          </button>
        </div>
      </form>

      {loading ? (
        <div className="text-center py-8">Loading bills...</div>
      ) : bills.length === 0 ? (
        <div className="text-center py-8 text-gray-600">No bills yet. Add one to get started!</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 border-b">
                <th className="px-4 py-3 text-left">Member</th>
                <th className="px-4 py-3 text-left">Month</th>
                <th className="px-4 py-3 text-left">Water</th>
                <th className="px-4 py-3 text-left">Electricity</th>
                <th className="px-4 py-3 text-left">Balance</th>
                <th className="px-4 py-3 text-left">Status</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {bills.map(b => (
                <BillRow key={b.id} b={b} onDelete={remove} onSave={save} />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
