import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchSchedules, addSchedule, updateSchedule, removeSchedule } from '../redux/scheduleSlice'
import { fetchMembers } from '../redux/memberSlice'
import api from '../services/api'

function ScheduleRow({ s, members, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    task_type: s.task_type,
    assigned_to: s.assigned_to || '',
    date: s.date,
    time: s.time,
    description: s.description || '',
    completed: s.completed || false,
  })
  const [saving, setSaving] = useState(false)

  const save = async () => {
    setSaving(true)
    try {
      await onSave(s.id, form)
      setEditing(false)
    } catch (err) {
      console.error('Error saving schedule:', err)
    } finally {
      setSaving(false)
    }
  }

  const assignedMember = s.assigned_to ? (members.find(m => m.id === s.assigned_to) || { name: s.member_name || 'N/A' }) : null
  const memberDisplay = assignedMember ? assignedMember.name : (s.member_name || '-')

  if (editing) {
    return (
      <tr className="border-t bg-blue-50">
        <td className="px-4 py-2">
          <select className="border p-1 w-full" value={form.task_type} onChange={e => setForm({ ...form, task_type: e.target.value })}>
            <option>Water</option>
            <option>Food</option>
            <option>Cleaning</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <select className="border p-1 w-full" value={form.assigned_to || ''} onChange={e => setForm({ ...form, assigned_to: e.target.value || null })}>
            <option value="">None</option>
            {members.map(m => (
              <option key={m.id} value={m.id}>{m.name} {m.room_number ? `(Room ${m.room_number})` : ''}</option>
            ))}
          </select>
        </td>
        <td className="px-4 py-2"><input type="date" className="border p-1 w-full" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} /></td>
        <td className="px-4 py-2"><input type="time" className="border p-1 w-full" value={form.time} onChange={e => setForm({ ...form, time: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} /></td>
        <td className="px-4 py-2">
          <input type="checkbox" checked={form.completed} onChange={e => setForm({ ...form, completed: e.target.checked })} />
        </td>
        <td className="px-4 py-2">
          <button onClick={save} disabled={saving} className="mr-2 text-sm text-green-600 hover:text-green-800 disabled:text-gray-400">{saving ? 'Saving...' : 'Save'}</button>
          <button onClick={() => setEditing(false)} disabled={saving} className="text-sm text-gray-600 hover:text-gray-800 disabled:text-gray-400">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t hover:bg-gray-50">
      <td className="px-4 py-2">{s.task_type}</td>
      <td className="px-4 py-2">{memberDisplay}</td>
      <td className="px-4 py-2">{s.date}</td>
      <td className="px-4 py-2">{s.time}</td>
      <td className="px-4 py-2">{s.description || '-'}</td>
      <td className="px-4 py-2">
        <span className={s.completed ? 'text-green-600' : 'text-yellow-600'}>
          {s.completed ? 'Completed' : 'Pending'}
        </span>
      </td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600 hover:text-blue-800">Edit</button>
        <button onClick={() => onDelete(s.id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
      </td>
    </tr>
  )
}

export default function Schedules() {
  const dispatch = useDispatch()
  const { list: schedules, status } = useSelector(state => state.schedules)
  const { list: members } = useSelector(state => state.members)
  const [form, setForm] = useState({ task_type: 'Water', assigned_to: '', date: '', time: '', description: '', completed: false })
  const [submitError, setSubmitError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    dispatch(fetchSchedules())
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setSubmitError(null)
    try {
      const scheduleData = { ...form, assigned_to: form.assigned_to ? parseInt(form.assigned_to) : null }
      const res = await api.post('/schedules/', scheduleData)
      dispatch(addSchedule(res.data))
      setForm({ task_type: 'Water', assigned_to: '', date: '', time: '', description: '', completed: false })
    } catch (err) {
      console.error('[SCHEDULES] Error creating schedule:', err.response?.data || err.message)
      setSubmitError('Failed to create schedule: ' + (err.response?.data?.detail || err.message))
    } finally {
      setSubmitting(false)
    }
  }

  const remove = async (id) => {
    if (!confirm('Delete schedule?')) return
    try {
      await api.delete(`/schedules/${id}/`)
      dispatch(removeSchedule(id))
    } catch (err) {
      console.error('[SCHEDULES] Error deleting schedule:', err)
      alert('Failed to delete schedule')
    }
  }

  const save = async (id, data) => {
    try {
      const scheduleData = { ...data, assigned_to: data.assigned_to ? parseInt(data.assigned_to) : null }
      const res = await api.patch(`/schedules/${id}/`, scheduleData)
      dispatch(updateSchedule(res.data))
    } catch (err) {
      console.error('[SCHEDULES] Error updating schedule:', err)
      alert('Failed to update schedule')
    }
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Schedules</h2>

      <form onSubmit={create} className="mb-8 space-y-3 max-w-lg bg-gray-50 p-4 rounded">
        <h3 className="font-semibold">Add New Schedule</h3>
        <div>
          <label className="block text-sm font-medium mb-1">Task Type *</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.task_type} 
            onChange={e => setForm({ ...form, task_type: e.target.value })}
            required
            disabled={submitting}
          >
            <option>Water</option>
            <option>Food</option>
            <option>Cleaning</option>
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Assigned To</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.assigned_to} 
            onChange={e => setForm({ ...form, assigned_to: e.target.value })}
            disabled={submitting}
          >
            <option value="">None</option>
            {members.map(m => (
              <option key={m.id} value={m.id}>{m.name} {m.room_number ? `(Room ${m.room_number})` : ''}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Date *</label>
          <input 
            type="date" 
            className="border p-2 w-full rounded" 
            value={form.date} 
            onChange={e => setForm({ ...form, date: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Time *</label>
          <input 
            type="time" 
            className="border p-2 w-full rounded" 
            value={form.time} 
            onChange={e => setForm({ ...form, time: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Description</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Description" 
            value={form.description} 
            onChange={e => setForm({ ...form, description: e.target.value })}
            disabled={submitting}
          />
        </div>
        {submitError && <div className="text-red-600 bg-red-50 p-2 rounded text-sm">{submitError}</div>}
        <div>
          <button 
            type="submit"
            disabled={submitting || !form.date || !form.time}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {submitting ? 'Adding...' : 'Add Schedule'}
          </button>
        </div>
      </form>

      {loading ? (
        <div className="text-center py-8">Loading schedules...</div>
      ) : schedules.length === 0 ? (
        <div className="text-center py-8 text-gray-600">No schedules yet. Add one to get started!</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 border-b">
                <th className="px-4 py-3 text-left">Type</th>
                <th className="px-4 py-3 text-left">Assigned To</th>
                <th className="px-4 py-3 text-left">Date</th>
                <th className="px-4 py-3 text-left">Time</th>
                <th className="px-4 py-3 text-left">Description</th>
                <th className="px-4 py-3 text-left">Status</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {schedules.map(s => (
                <ScheduleRow key={s.id} s={s} members={members} onDelete={remove} onSave={save} />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
