import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchPayments, addPayment, updatePayment, removePayment } from '../redux/paymentSlice'
import { fetchMembers } from '../redux/memberSlice'
import api from '../services/api'

function PaymentRow({ p, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    amount: p.amount,
    collected_by: p.collected_by || '',
    status: p.status,
  })
  const [saving, setSaving] = useState(false)

  const save = async () => {
    setSaving(true)
    try {
      await onSave(p.id, form)
      setEditing(false)
    } catch (err) {
      console.error('Error saving payment:', err)
    } finally {
      setSaving(false)
    }
  }

  const memberDisplay = p.member_name || (typeof p.member === 'object' ? p.member?.name : p.member) || 'N/A'

  if (editing) {
    return (
      <tr className="border-t bg-blue-50">
        <td className="px-4 py-2">{memberDisplay}</td>
        <td className="px-4 py-2"><input className="border p-1 w-full" type="number" step="0.01" value={form.amount} onChange={e => setForm({ ...form, amount: e.target.value })} /></td>
        <td className="px-4 py-2">{p.payment_date}</td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.collected_by} onChange={e => setForm({ ...form, collected_by: e.target.value })} /></td>
        <td className="px-4 py-2">
          <select className="border p-1 w-full" value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <button onClick={save} disabled={saving} className="mr-2 text-sm text-green-600 hover:text-green-800 disabled:text-gray-400">{saving ? 'Saving...' : 'Save'}</button>
          <button onClick={() => setEditing(false)} disabled={saving} className="text-sm text-gray-600 hover:text-gray-800 disabled:text-gray-400">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t hover:bg-gray-50">
      <td className="px-4 py-2">{memberDisplay}</td>
      <td className="px-4 py-2">â‚¹{p.amount}</td>
      <td className="px-4 py-2">{p.payment_date}</td>
      <td className="px-4 py-2">{p.collected_by || '-'}</td>
      <td className="px-4 py-2"><span className={p.status === 'Paid' ? 'text-green-600' : 'text-red-600'}>{p.status}</span></td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600 hover:text-blue-800">Edit</button>
        <button onClick={() => onDelete(p.id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
      </td>
    </tr>
  )
}

export default function Payments() {
  const dispatch = useDispatch()
  const { list: payments, status } = useSelector(state => state.payments)
  const { list: members } = useSelector(state => state.members)
  const [form, setForm] = useState({ member: '', amount: '', collected_by: '', status: 'Paid' })
  const [submitError, setSubmitError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    dispatch(fetchPayments())
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setSubmitError(null)
    try {
      const paymentData = { ...form, member: parseInt(form.member) || null }
      const res = await api.post('/payments/', paymentData)
      dispatch(addPayment(res.data))
      setForm({ member: '', amount: '', collected_by: '', status: 'Paid' })
    } catch (err) {
      console.error('[PAYMENTS] Error creating payment:', err.response?.data || err.message)
      setSubmitError('Failed to create payment: ' + (err.response?.data?.detail || err.message))
    } finally {
      setSubmitting(false)
    }
  }

  const remove = async (id) => {
    if (!confirm('Delete payment?')) return
    try {
      await api.delete(`/payments/${id}/`)
      dispatch(removePayment(id))
    } catch (err) {
      console.error('[PAYMENTS] Error deleting payment:', err)
      alert('Failed to delete payment')
    }
  }

  const save = async (id, data) => {
    try {
      const res = await api.patch(`/payments/${id}/`, data)
      dispatch(updatePayment(res.data))
    } catch (err) {
      console.error('[PAYMENTS] Error updating payment:', err)
      alert('Failed to update payment')
    }
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Payments</h2>

      <form onSubmit={create} className="mb-8 space-y-3 max-w-lg bg-gray-50 p-4 rounded">
        <h3 className="font-semibold">Add New Payment</h3>
        <div>
          <label className="block text-sm font-medium mb-1">Member *</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.member} 
            onChange={e => setForm({ ...form, member: e.target.value })}
            required
            disabled={submitting}
          >
            <option value="">Select a member</option>
            {members.map(m => (
              <option key={m.id} value={m.id}>{m.name} {m.room_number ? `(Room ${m.room_number})` : ''}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Amount *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Amount" 
            type="number" 
            step="0.01" 
            value={form.amount} 
            onChange={e => setForm({ ...form, amount: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Collected By</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Collected by" 
            value={form.collected_by} 
            onChange={e => setForm({ ...form, collected_by: e.target.value })}
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Status</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.status} 
            onChange={e => setForm({ ...form, status: e.target.value })}
            disabled={submitting}
          >
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </div>
        {submitError && <div className="text-red-600 bg-red-50 p-2 rounded text-sm">{submitError}</div>}
        <div>
          <button 
            type="submit"
            disabled={submitting || !form.member || !form.amount}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {submitting ? 'Adding...' : 'Add Payment'}
          </button>
        </div>
      </form>

      {loading ? (
        <div className="text-center py-8">Loading payments...</div>
      ) : payments.length === 0 ? (
        <div className="text-center py-8 text-gray-600">No payments yet. Add one to get started!</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 border-b">
                <th className="px-4 py-3 text-left">Member</th>
                <th className="px-4 py-3 text-left">Amount</th>
                <th className="px-4 py-3 text-left">Date</th>
                <th className="px-4 py-3 text-left">Collected By</th>
                <th className="px-4 py-3 text-left">Status</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {payments.map(p => (
                <PaymentRow key={p.id} p={p} onDelete={remove} onSave={save} />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
