import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchRepairs, addRepair, updateRepair, removeRepair } from '../redux/repairSlice'
import { fetchMembers } from '../redux/memberSlice'
import api from '../services/api'

function RepairRow({ r, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    item_name: r.item_name,
    repair_date: r.repair_date,
    cost: r.cost,
    replaced_by: r.replaced_by || '',
    description: r.description || '',
    status: r.status,
  })
  const [saving, setSaving] = useState(false)

  const save = async () => {
    setSaving(true)
    try {
      await onSave(r.id, form)
      setEditing(false)
    } catch (err) {
      console.error('Error saving repair:', err)
    } finally {
      setSaving(false)
    }
  }

  const memberDisplay = r.member_name || (typeof r.member === 'object' ? r.member?.name : r.member) || 'N/A'

  if (editing) {
    return (
      <tr className="border-t bg-blue-50">
        <td className="px-4 py-2">{memberDisplay}</td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.item_name} onChange={e => setForm({ ...form, item_name: e.target.value })} /></td>
        <td className="px-4 py-2"><input type="date" className="border p-1 w-full" value={form.repair_date} onChange={e => setForm({ ...form, repair_date: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" type="number" step="0.01" value={form.cost} onChange={e => setForm({ ...form, cost: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.replaced_by} onChange={e => setForm({ ...form, replaced_by: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1 w-full" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} /></td>
        <td className="px-4 py-2">
          <select className="border p-1 w-full" value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
            <option>Pending</option>
            <option>Completed</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <button onClick={save} disabled={saving} className="mr-2 text-sm text-green-600 hover:text-green-800 disabled:text-gray-400">{saving ? 'Saving...' : 'Save'}</button>
          <button onClick={() => setEditing(false)} disabled={saving} className="text-sm text-gray-600 hover:text-gray-800 disabled:text-gray-400">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t hover:bg-gray-50">
      <td className="px-4 py-2">{memberDisplay}</td>
      <td className="px-4 py-2">{r.item_name}</td>
      <td className="px-4 py-2">{r.repair_date}</td>
      <td className="px-4 py-2">â‚¹{r.cost}</td>
      <td className="px-4 py-2">{r.replaced_by || '-'}</td>
      <td className="px-4 py-2">{r.description || '-'}</td>
      <td className="px-4 py-2"><span className={r.status === 'Completed' ? 'text-green-600' : 'text-yellow-600'}>{r.status}</span></td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600 hover:text-blue-800">Edit</button>
        <button onClick={() => onDelete(r.id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
      </td>
    </tr>
  )
}

export default function Repairs() {
  const dispatch = useDispatch()
  const { list: repairs, status } = useSelector(state => state.repairs)
  const { list: members } = useSelector(state => state.members)
  const [form, setForm] = useState({ member: '', item_name: '', repair_date: '', cost: '', replaced_by: '', description: '', status: 'Pending' })
  const [submitError, setSubmitError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    dispatch(fetchRepairs())
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setSubmitError(null)
    try {
      const repairData = { ...form, member: parseInt(form.member) || null }
      const res = await api.post('/repairs/', repairData)
      dispatch(addRepair(res.data))
      setForm({ member: '', item_name: '', repair_date: '', cost: '', replaced_by: '', description: '', status: 'Pending' })
    } catch (err) {
      console.error('[REPAIRS] Error creating repair:', err.response?.data || err.message)
      setSubmitError('Failed to create repair: ' + (err.response?.data?.detail || err.message))
    } finally {
      setSubmitting(false)
    }
  }

  const remove = async (id) => {
    if (!confirm('Delete repair?')) return
    try {
      await api.delete(`/repairs/${id}/`)
      dispatch(removeRepair(id))
    } catch (err) {
      console.error('[REPAIRS] Error deleting repair:', err)
      alert('Failed to delete repair')
    }
  }

  const save = async (id, data) => {
    try {
      const res = await api.patch(`/repairs/${id}/`, data)
      dispatch(updateRepair(res.data))
    } catch (err) {
      console.error('[REPAIRS] Error updating repair:', err)
      alert('Failed to update repair')
    }
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Repairs</h2>

      <form onSubmit={create} className="mb-8 space-y-3 max-w-lg bg-gray-50 p-4 rounded">
        <h3 className="font-semibold">Add New Repair</h3>
        <div>
          <label className="block text-sm font-medium mb-1">Member *</label>
          <select 
            className="border p-2 w-full rounded" 
            value={form.member} 
            onChange={e => setForm({ ...form, member: e.target.value })}
            required
            disabled={submitting}
          >
            <option value="">Select a member</option>
            {members.map(m => (
              <option key={m.id} value={m.id}>{m.name} {m.room_number ? `(Room ${m.room_number})` : ''}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Item Name *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Item name" 
            value={form.item_name} 
            onChange={e => setForm({ ...form, item_name: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Repair Date *</label>
          <input 
            type="date" 
            className="border p-2 w-full rounded" 
            value={form.repair_date} 
            onChange={e => setForm({ ...form, repair_date: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Cost *</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Cost" 
            type="number" 
            step="0.01" 
            value={form.cost} 
            onChange={e => setForm({ ...form, cost: e.target.value })}
            required
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Replaced By</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Replaced by" 
            value={form.replaced_by} 
            onChange={e => setForm({ ...form, replaced_by: e.target.value })}
            disabled={submitting}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Description</label>
          <input 
            className="border p-2 w-full rounded" 
            placeholder="Description" 
            value={form.description} 
            onChange={e => setForm({ ...form, description: e.target.value })}
            disabled={submitting}
          />
        </div>
        {submitError && <div className="text-red-600 bg-red-50 p-2 rounded text-sm">{submitError}</div>}
        <div>
          <button 
            type="submit"
            disabled={submitting || !form.member || !form.item_name || !form.repair_date || !form.cost}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {submitting ? 'Adding...' : 'Add Repair'}
          </button>
        </div>
      </form>

      {loading ? (
        <div className="text-center py-8">Loading repairs...</div>
      ) : repairs.length === 0 ? (
        <div className="text-center py-8 text-gray-600">No repairs yet. Add one to get started!</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 border-b">
                <th className="px-4 py-3 text-left">Member</th>
                <th className="px-4 py-3 text-left">Item</th>
                <th className="px-4 py-3 text-left">Date</th>
                <th className="px-4 py-3 text-left">Cost</th>
                <th className="px-4 py-3 text-left">Replaced By</th>
                <th className="px-4 py-3 text-left">Description</th>
                <th className="px-4 py-3 text-left">Status</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {repairs.map(r => (
                <RepairRow key={r.id} r={r} onDelete={remove} onSave={save} />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
