import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import api from '../services/api'

export const fetchMembers = createAsyncThunk('members/fetch', async () => {
  const res = await api.get('/members/')
  return res.data
})

const memberSlice = createSlice({
  name: 'members',
  initialState: { list: [], status: 'idle' },
  reducers: {
    addMember: (state, action) => {
      state.list.push(action.payload)
    },
    updateMember: (state, action) => {
      const idx = state.list.findIndex(m => m.id === action.payload.id)
      if (idx >= 0) state.list[idx] = action.payload
    },
    removeMember: (state, action) => {
      state.list = state.list.filter(m => m.id !== action.payload)
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchMembers.pending, (state) => { state.status = 'loading' })
      .addCase(fetchMembers.fulfilled, (state, action) => {
        state.list = action.payload
        state.status = 'succeeded'
      })
      .addCase(fetchMembers.rejected, (state) => { state.status = 'failed' })
  }
})

export const { addMember, updateMember, removeMember } = memberSlice.actions
export default memberSlice.reducer
