import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import api from '../services/api'

export const fetchSchedules = createAsyncThunk('schedules/fetch', async () => {
  const res = await api.get('/schedules/')
  return res.data
})

const scheduleSlice = createSlice({
  name: 'schedules',
  initialState: { list: [], status: 'idle' },
  reducers: {
    addSchedule: (state, action) => {
      state.list.push(action.payload)
    },
    updateSchedule: (state, action) => {
      const idx = state.list.findIndex(s => s.id === action.payload.id)
      if (idx >= 0) state.list[idx] = action.payload
    },
    removeSchedule: (state, action) => {
      state.list = state.list.filter(s => s.id !== action.payload)
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchSchedules.pending, (state) => { state.status = 'loading' })
      .addCase(fetchSchedules.fulfilled, (state, action) => {
        state.list = action.payload
        state.status = 'succeeded'
      })
      .addCase(fetchSchedules.rejected, (state) => { state.status = 'failed' })
  }
})

export const { addSchedule, updateSchedule, removeSchedule } = scheduleSlice.actions
export default scheduleSlice.reducer
