import React, {useEffect, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchMembers, addMember, updateMember, removeMember } from '../redux/memberSlice'
import api from '../services/api'

function MemberRow({m, onDelete, onSave}){
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({name: m.name, email: m.email, room_number: m.room_number || ''})

  const save = async ()=>{
    await onSave(m.id, form)
    setEditing(false)
  }

  if(editing){
    return (
      <tr className="border-t">
        <td className="px-4 py-2"><input className="border p-1" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} /></td>
        <td className="px-4 py-2"><input className="border p-1" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} /></td>
        <td className="px-4 py-2"><input className="border p-1" value={form.room_number} onChange={e=>setForm({...form, room_number:e.target.value})} /></td>
        <td className="px-4 py-2">
          <button onClick={save} className="mr-2 text-sm text-green-600">Save</button>
          <button onClick={()=>setEditing(false)} className="text-sm text-gray-600">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t">
      <td className="px-4 py-2">{m.name}</td>
      <td className="px-4 py-2">{m.email}</td>
      <td className="px-4 py-2">{m.room_number}</td>
      <td className="px-4 py-2">
        <button onClick={()=>setEditing(true)} className="mr-2 text-sm text-blue-600">Edit</button>
        <button onClick={()=>onDelete(m.id)} className="text-sm text-red-600">Delete</button>
      </td>
    </tr>
  )
}

export default function Members(){
  const dispatch = useDispatch()
  const { list: members, status } = useSelector(state => state.members)
  const [form, setForm] = useState({name:'', email:'', room_number:''})

  useEffect(()=>{
    dispatch(fetchMembers())
  }, [dispatch])

  const create = async (e)=>{
    e.preventDefault()
    try{
      const res = await api.post('/members/', form)
      dispatch(addMember(res.data))
      setForm({name:'', email:'', room_number:''})
    }catch(err){ console.error(err) }
  }

  const remove = async (id)=>{
    if(!confirm('Delete member?')) return
    await api.delete(`/members/${id}/`)
    dispatch(removeMember(id))
  }

  const save = async (id, data)=>{
    const res = await api.patch(`/members/${id}/`, data)
    dispatch(updateMember(res.data))
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Members</h2>

      <form onSubmit={create} className="mb-6 space-y-2 max-w-lg">
        <div>
          <input className="border p-2 w-full" placeholder="Name" value={form.name} onChange={e=>setForm({...form, name: e.target.value})} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Email" value={form.email} onChange={e=>setForm({...form, email: e.target.value})} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Room number" value={form.room_number} onChange={e=>setForm({...form, room_number: e.target.value})} />
        </div>
        <div>
          <button className="bg-green-600 text-white px-3 py-1 rounded">Add member</button>
        </div>
      </form>

      {loading ? <div>Loading...</div> : (
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Email</th>
              <th className="px-4 py-2">Room</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {members.map(m => (
              <MemberRow key={m.id} m={m} onDelete={remove} onSave={save} />
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
