import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchSchedules, addSchedule, updateSchedule, removeSchedule } from '../redux/scheduleSlice'
import api from '../services/api'

function ScheduleRow({ s, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    task_type: s.task_type,
    date: s.date,
    time: s.time,
    description: s.description || '',
  })

  const save = async () => {
    await onSave(s.id, form)
    setEditing(false)
  }

  if (editing) {
    return (
      <tr className="border-t">
        <td className="px-4 py-2">
          <select className="border p-1" value={form.task_type} onChange={e => setForm({ ...form, task_type: e.target.value })}>
            <option>Water</option>
            <option>Food</option>
            <option>Cleaning</option>
          </select>
        </td>
        <td className="px-4 py-2"><input className="border p-1" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1" value={form.time} onChange={e => setForm({ ...form, time: e.target.value })} /></td>
        <td className="px-4 py-2"><input className="border p-1" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} /></td>
        <td className="px-4 py-2">
          <button onClick={save} className="mr-2 text-sm text-green-600">Save</button>
          <button onClick={() => setEditing(false)} className="text-sm text-gray-600">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t">
      <td className="px-4 py-2">{s.task_type}</td>
      <td className="px-4 py-2">{s.date}</td>
      <td className="px-4 py-2">{s.time}</td>
      <td className="px-4 py-2">{s.description}</td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600">Edit</button>
        <button onClick={() => onDelete(s.id)} className="text-sm text-red-600">Delete</button>
      </td>
    </tr>
  )
}

export default function Schedules() {
  const dispatch = useDispatch()
  const { list: schedules, status } = useSelector(state => state.schedules)
  const [form, setForm] = useState({ task_type: 'Water', date: '', time: '', description: '' })

  useEffect(() => {
    dispatch(fetchSchedules())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    try {
      const res = await api.post('/schedules/', form)
      dispatch(addSchedule(res.data))
      setForm({ task_type: 'Water', date: '', time: '', description: '' })
    } catch (err) { console.error(err) }
  }

  const remove = async (id) => {
    if (!confirm('Delete schedule?')) return
    await api.delete(`/schedules/${id}/`)
    dispatch(removeSchedule(id))
  }

  const save = async (id, data) => {
    const res = await api.patch(`/schedules/${id}/`, data)
    dispatch(updateSchedule(res.data))
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Schedules</h2>

      <form onSubmit={create} className="mb-6 space-y-2 max-w-lg">
        <div>
          <select className="border p-2 w-full" value={form.task_type} onChange={e => setForm({ ...form, task_type: e.target.value })}>
            <option>Water</option>
            <option>Food</option>
            <option>Cleaning</option>
          </select>
        </div>
        <div>
          <input type="date" className="border p-2 w-full" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} />
        </div>
        <div>
          <input type="time" className="border p-2 w-full" value={form.time} onChange={e => setForm({ ...form, time: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} />
        </div>
        <button className="bg-green-600 text-white px-3 py-1 rounded">Add Schedule</button>
      </form>

      {loading ? <div>Loading...</div> : (
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Type</th>
              <th className="px-4 py-2">Date</th>
              <th className="px-4 py-2">Time</th>
              <th className="px-4 py-2">Description</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {schedules.map(s => (
              <ScheduleRow key={s.id} s={s} onDelete={remove} onSave={save} />
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
