import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchPayments, addPayment, updatePayment, removePayment } from '../redux/paymentSlice'
import api from '../services/api'

function PaymentRow({ p, onDelete, onSave }) {
  const [editing, setEditing] = useState(false)
  const [form, setForm] = useState({
    amount: p.amount,
    collected_by: p.collected_by || '',
    status: p.status,
  })

  const save = async () => {
    await onSave(p.id, form)
    setEditing(false)
  }

  if (editing) {
    return (
      <tr className="border-t">
        <td className="px-4 py-2">{p.member}</td>
        <td className="px-4 py-2"><input className="border p-1" type="number" step="0.01" value={form.amount} onChange={e => setForm({ ...form, amount: e.target.value })} /></td>
        <td className="px-4 py-2">{p.payment_date}</td>
        <td className="px-4 py-2"><input className="border p-1" value={form.collected_by} onChange={e => setForm({ ...form, collected_by: e.target.value })} /></td>
        <td className="px-4 py-2">
          <select className="border p-1" value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </td>
        <td className="px-4 py-2">
          <button onClick={save} className="mr-2 text-sm text-green-600">Save</button>
          <button onClick={() => setEditing(false)} className="text-sm text-gray-600">Cancel</button>
        </td>
      </tr>
    )
  }

  return (
    <tr className="border-t">
      <td className="px-4 py-2">{p.member}</td>
      <td className="px-4 py-2">{p.amount}</td>
      <td className="px-4 py-2">{p.payment_date}</td>
      <td className="px-4 py-2">{p.collected_by}</td>
      <td className="px-4 py-2"><span className={p.status === 'Paid' ? 'text-green-600' : 'text-red-600'}>{p.status}</span></td>
      <td className="px-4 py-2">
        <button onClick={() => setEditing(true)} className="mr-2 text-sm text-blue-600">Edit</button>
        <button onClick={() => onDelete(p.id)} className="text-sm text-red-600">Delete</button>
      </td>
    </tr>
  )
}

export default function Payments() {
  const dispatch = useDispatch()
  const { list: payments, status } = useSelector(state => state.payments)
  const [form, setForm] = useState({ member: '', amount: '', collected_by: '', status: 'Paid' })

  useEffect(() => {
    dispatch(fetchPayments())
  }, [dispatch])

  const create = async (e) => {
    e.preventDefault()
    try {
      const paymentData = { ...form, member: parseInt(form.member) || null }
      const res = await api.post('/payments/', paymentData)
      dispatch(addPayment(res.data))
      setForm({ member: '', amount: '', collected_by: '', status: 'Paid' })
    } catch (err) { console.error(err) }
  }

  const remove = async (id) => {
    if (!confirm('Delete payment?')) return
    await api.delete(`/payments/${id}/`)
    dispatch(removePayment(id))
  }

  const save = async (id, data) => {
    const res = await api.patch(`/payments/${id}/`, data)
    dispatch(updatePayment(res.data))
  }

  const loading = status === 'loading'

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Payments</h2>

      <form onSubmit={create} className="mb-6 space-y-2 max-w-lg">
        <div>
          <input className="border p-2 w-full" placeholder="Member ID" type="number" value={form.member} onChange={e => setForm({ ...form, member: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Amount" type="number" step="0.01" value={form.amount} onChange={e => setForm({ ...form, amount: e.target.value })} />
        </div>
        <div>
          <input className="border p-2 w-full" placeholder="Collected by" value={form.collected_by} onChange={e => setForm({ ...form, collected_by: e.target.value })} />
        </div>
        <div>
          <select className="border p-2 w-full" value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
            <option>Paid</option>
            <option>Unpaid</option>
          </select>
        </div>
        <button className="bg-green-600 text-white px-3 py-1 rounded">Add Payment</button>
      </form>

      {loading ? <div>Loading...</div> : (
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Member</th>
              <th className="px-4 py-2">Amount</th>
              <th className="px-4 py-2">Date</th>
              <th className="px-4 py-2">Collected By</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {payments.map(p => (
              <PaymentRow key={p.id} p={p} onDelete={remove} onSave={save} />
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
