import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import api from '../services/api'

export const fetchPayments = createAsyncThunk('payments/fetch', async () => {
  const res = await api.get('/payments/')
  return res.data
})

const paymentSlice = createSlice({
  name: 'payments',
  initialState: { list: [], status: 'idle' },
  reducers: {
    addPayment: (state, action) => {
      state.list.push(action.payload)
    },
    updatePayment: (state, action) => {
      const idx = state.list.findIndex(p => p.id === action.payload.id)
      if (idx >= 0) state.list[idx] = action.payload
    },
    removePayment: (state, action) => {
      state.list = state.list.filter(p => p.id !== action.payload)
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchPayments.pending, (state) => { state.status = 'loading' })
      .addCase(fetchPayments.fulfilled, (state, action) => {
        state.list = action.payload
        state.status = 'succeeded'
      })
      .addCase(fetchPayments.rejected, (state) => { state.status = 'failed' })
  }
})

export const { addPayment, updatePayment, removePayment } = paymentSlice.actions
export default paymentSlice.reducer
