import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import api from '../services/api'

export const fetchBills = createAsyncThunk('bills/fetch', async () => {
  const res = await api.get('/bills/')
  return res.data
})

const billSlice = createSlice({
  name: 'bills',
  initialState: { list: [], status: 'idle' },
  reducers: {
    addBill: (state, action) => {
      state.list.push(action.payload)
    },
    updateBill: (state, action) => {
      const idx = state.list.findIndex(b => b.id === action.payload.id)
      if (idx >= 0) state.list[idx] = action.payload
    },
    removeBill: (state, action) => {
      state.list = state.list.filter(b => b.id !== action.payload)
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchBills.pending, (state) => { state.status = 'loading' })
      .addCase(fetchBills.fulfilled, (state, action) => {
        state.list = action.payload
        state.status = 'succeeded'
      })
      .addCase(fetchBills.rejected, (state) => { state.status = 'failed' })
  }
})

export const { addBill, updateBill, removeBill } = billSlice.actions
export default billSlice.reducer
